COMPONENT.script:
			initialize()=
declare

enddeclare
{
	url = CurSession.getenv(name = 'ORJSON_URL');
	IF url = '' THEN	
		url = 'http://localhost:8080/openroad/comtest';
	ELSE
		url = url + '/comtest'
	ENDIF;
	hellostring = 'HELLO';
	counter = 1;
}

ON CLICK btn_helloworld =
declare
	request = StringObject;
	response = StringObject;
	jsonHand = JsonHandler;
	responseObj = JsonObject;
	errorObj = JsonObject;
	resultObj = JsonObject;
	byrefResultObj = JsonObject;
enddeclare
{
	IF CurRemoteServer.Initiate(image ='', type = RP_SHARED, location=url, routing='http-jsonrpc', flags='') != ER_OK THEN
		CurFrame.InfoPopup( MessageText = 'Error initiating server connection:' + '0x' + hex(CurRemoteServer.ErrorCode)+ HC_NEWLINE + CurRemoteServer.ErrorText,
							MessageType = MT_ERROR);
	ELSE
		request.Value = '{"jsonrpc": "2.0", "method": "helloworld", "params": {"counter": '+ VARCHAR(counter) + ', "hellostring": "' + hellostring + '", "$byref_params":"hellostring,counter"}, "id": 1}';

		response = CurRemoteServer.JsonRpcRequest(request=request);

		responseObj = JsonObject(jsonHand.Parse(json = response));

		errorObj = JsonObject(responseObj.GetMember(name = 'error'));

		IF errorObj IS NOT NULL THEN
			CurFrame.InfoPopup(MessageText = 'Error Code: ' + JsonNumber(errorObj.GetMember(name = 'code')).TextValue + HC_NEWLINE + 'Error Message: ' + JsonString(errorObj.GetMember(name = 'message')).Value.Value,
							   MessageType = MT_ERROR);
		ELSE
			resultObj = JsonObject(responseObj.GetMember(name = 'result'));
			byrefResultObj = JsonObject(resultObj.GetMember(name = 'byref_results'));

			result_counter = JsonNumber(byrefResultObj.GetMember(name = 'counter')).TextValue;
			result_hellostring = JsonString(byrefResultObj.GetMember(name = 'hellostring')).Value;
		ENDIF;
	ENDIF;
	CurRemoteServer.Release();		
}

