COMPONENT.script:
			initialize()=
declare
enddeclare
{
	url = CurSession.getenv(name = 'ORJSON_URL');
	IF url = '' THEN
		url = 'http://localhost:8080/openroad/jsonrpcservertest';
	ELSE
		url = url + '/jsonrpcservertest'
	ENDIF;

	field(request).UpdateBias = FB_DIMMED ;
	field(response).UpdateBias = FB_DIMMED ;
	field(btn_JsonRpcRequest).UpdateBias = FB_DIMMED ;
	field(btn_release).UpdateBias = FB_DIMMED ;
}

ON CLICK btn_initiate =
{
	IF CurRemoteServer.Initiate(image = '', type = RP_SHARED, location = url, routing = 'http-jsonrpc', flags = '') != ER_OK THEN
		CurFrame.InfoPopup( messagetext = 'Error initiating server connection:' + '0x' + hex(CurRemoteServer.ErrorCode)+ HC_NEWLINE + CurRemoteServer.ErrorText,
							messagetype = MT_ERROR);
		CurRemoteServer.Release();
	ELSE
		field(btn_initiate).UpdateBias = FB_DIMMED ;
		field(url).UpdateBias = FB_DIMMED ;

		field(request).UpdateBias = FB_CHANGEABLE ;
		request.value = '{"jsonrpc": "2.0", "id": 1, "method": "subtract", "params": {"subtrahend": 23.4, "minuend": 42.8}}';
	    field(response).UpdateBias = FB_LANDABLE ;

		field(btn_JsonRpcRequest).UpdateBias  = FB_CHANGEABLE ;
		field(btn_release).UpdateBias  = FB_CHANGEABLE ;
	ENDIF;
}

ON CLICK btn_JsonRpcRequest =
{
	response = CurRemoteServer.JsonRpcRequest(request=request);
}

ON CLICK btn_release =
{
	request.Value = '';
	response.Value = '';
	CurRemoteServer.Release();

	field(btn_initiate).UpdateBias = FB_CHANGEABLE ;
	field(url).UpdateBias = FB_CHANGEABLE ;
	field(request).UpdateBias = FB_DIMMED ;
	field(response).UpdateBias = FB_DIMMED ;
	field(btn_JsonRpcRequest).UpdateBias  = FB_DIMMED ;
	field(btn_release).UpdateBias  = FB_DIMMED ;
}

