COMPONENT.script:
			initialize()=
declare

enddeclare
{
	url = CurSession.getenv(name = 'ORJSON_URL');
	IF url = '' THEN	
		url = 'http://localhost:8080/openroad/jsonrpcservertest';
	ELSE
		url = url + '/jsonrpcservertest'
	ENDIF;
	minuend = 42.8;
	subtrahend = 23.4;
}

ON CLICK btn_subtract =
declare
	request = StringObject;
	response = StringObject;
	jhand = JsonHandler;
	responseObj = JsonObject;
	resultObj = JsonNumber;
	errorObj = JsonObject;
enddeclare
{
	IF CurRemoteServer.Initiate(image ='', type = RP_SHARED, location=url, routing='http-jsonrpc', flags='') != ER_OK THEN
		CurFrame.InfoPopup( MessageText = 'Error initiating server connection:' + '0x' + hex(CurRemoteServer.ErrorCode)+ HC_NEWLINE + CurRemoteServer.ErrorText,
							MessageType = MT_ERROR);
	ELSE
		request.Value = '{"jsonrpc": "2.0", "method": "subtract", "params": {"subtrahend": '+ VARCHAR(subtrahend) + ', "minuend": ' + VARCHAR(minuend) + '}, "id": 1}';

		response = CurRemoteServer.JsonRpcRequest(request = request);

		responseObj = JsonObject(jhand.Parse(json = response));

		errorObj = JsonObject(responseObj.GetMember(name = 'error'));

		IF errorObj IS NOT NULL THEN
			CurFrame.InfoPopup( MessageText = 'Error Code: ' + JsonNumber(errorObj.GetMember(name = 'code')).TextValue + HC_NEWLINE + 'Error Message: ' + JsonString(errorObj.GetMember(name = 'message')).Value.Value,
								MessageType = MT_ERROR);
		ELSE
			resultObj = JsonNumber(responseObj.GetMember(name = 'result'));
			result = resultObj.TextValue;
		ENDIF;
	ENDIF;
	CurRemoteServer.Release();		
}

