COMPONENT.script:
			PROCEDURE get_procedure_parameter_names
(
	procsrc	= Proc4GLSource
) =
DECLARE
	params		= ARRAY of StringObject;
	p		= INTEGER NOT NULL;
	plparen		= INTEGER NOT NULL;
	prparen		= INTEGER NOT NULL;
	raw_script	= StringObject DEFAULT NULL;
	ret		= INTEGER NOT NULL;
	so		= StringObject;
	soparams	= StringObject;
	str		= VARCHAR(2000) NOT NULL;
	scrcomposite	= ScriptComposite;
	strip_comments	= PROCEDURE returning INTEGER NOT NULL;
	get_parameters	= PROCEDURE returning INTEGER NOT NULL;
	test_procedure	= PROCEDURE returning INTEGER NOT NULL;
	find_matching_quote	= PROCEDURE returning INTEGER NOT NULL;
	find_matching_paren	= PROCEDURE returning INTEGER NOT NULL;
	substitute_in_string	= PROCEDURE returning VARCHAR(2000) NOT NULL;
ENDDECLARE
{
	params.clear();

	/*
	**  Get the preprocessed script.
	*/
	IF scrcomposite.quickmake(procsrc) = ER_OK THEN
		raw_script = scrcomposite.script
	ELSE
		RETURN NULL;
	ENDIF;

	/*
	**  Strip off the comments
	*/
	IF strip_comments(so = raw_script, dest = so) != ER_OK THEN
		RETURN NULL;;
	ENDIF;

	/*
	**  Find 'procedure'.
	*/
	p = so.LocateString(match = 'procedure', startposition = 1, ignorecase = TRUE);

	IF (p = 0) THEN
		RETURN NULL;
	ENDIF;

	IF test_procedure(so = so, curpos = p) != ER_OK THEN
		RETURN NULL;
	ENDIF;

	p = p + LENGTH('procedure');

	plparen = so.LocateString(match = '(', startposition = p);
	IF (plparen = 0) THEN
		CurExec.Trace(text = 'ERR_BAD_PROC_DECLARATION');
		RETURN NULL;
	ENDIF;

	/*
	**  Extract the PROCEDURE name.
	*/
	str = so.SubString(startposition = p, length = plparen - p);
	str = LOWERCASE(squeeze(str));

	IF (str != LOWERCASE(procsrc.name)) THEN
		CurExec.Trace(text = 'ERR_PROCNAME_MISMATCH');
		RETURN NULL;
	ENDIF;

	/*
	**  Having found the opening parenthesis, find the
	**  matching closing parenthesis.
	*/
	p = plparen + 1;
	prparen = find_matching_paren(so = so, curpos = p);
	IF (prparen = 0) THEN
		CurExec.Trace(text = 'ERR_UNMATCHED_PARENTHESIS');
		RETURN NULL;
	ENDIF;

	/*
	**  The parameters are between the matching parentheses.
	**  Extract the parameter list.
	*/
	soparams = so.ExtractString(startposition = p, length = prparen - p);

	/*
	**  Get the parameter list.
	*/
	IF get_parameters(so = soparams, params = params) != ER_OK THEN
		RETURN NULL;
	ENDIF;
	RETURN params;
}


/*
**  Having found 'procedure', this PROCEDURE tests to see IF
**  'procedure' can be the beginning of a PROCEDURE
**  declaration.  For this to be the beginning of
**  a declaration, it must be preceded AND followed
**  by white space.
*/
PROCEDURE test_procedure
(
	so	= StringObject,
	curpos	= INTEGER NOT NULL
) =
DECLARE
	j	= INTEGER NOT NULL;
	len	= INTEGER NOT NULL;
	str	= VARCHAR(10);
ENDDECLARE
{
	len = LENGTH('procedure');
	IF (curpos > 1) THEN
		j = curpos - 1;
		len = len + 2;
	ELSE
		j = curpos;
		len = len + 1;
	ENDIF;

	str = so.SubString(startposition = j, length = len);
	IF (LOWERCASE(squeeze(str)) != 'procedure') THEN
		CurExec.Trace(text = 'ERR_BAD_PROC_DECLARATION');
		RETURN ER_FAIL;
	ENDIF;

	RETURN ER_OK;
}


PROCEDURE strip_comments
(
	so		= StringObject,		// processed script
	dest	= StringObject		// script with comments removed
) =
DECLARE
	curpos		= INTEGER NOT NULL;
	incomment	= INTEGER NOT NULL;
	pend		= INTEGER NOT NULL;
	pcomment	= INTEGER NOT NULL;
	pcommentcpp	= INTEGER NOT NULL;
	pcopystart	= INTEGER NOT NULL;
	pcopyto		= INTEGER NOT NULL;
	pnext		= INTEGER NOT NULL;
	pquote		= INTEGER NOT NULL;
	slen		= INTEGER NOT NULL;
ENDDECLARE
{
	dest.value = '';

	slen = so.length;

	incomment	= FALSE;
	curpos	= 1;
	pquote	= 0;
	pcomment	= 0;
	pcommentcpp	= 0;
	pcopystart	= 1;
	pcopyto	= 0;

	WHILE (curpos < slen) DO

		IF (incomment = FALSE) THEN
			IF (pquote <= slen AND pquote < curpos) THEN
				pquote = so.LocateString(match = HC_QUOTE, startposition = curpos);
				IF (pquote = 0) THEN
					/*
					**  No more quotes
					*/
					pquote = slen + 1;
				ENDIF;
			ENDIF;

			IF (pcommentcpp <= slen AND pcommentcpp < curpos) THEN
				pcommentcpp = so.LocateString(match = '//', startposition = curpos);
				IF (pcommentcpp = 0) THEN
					/*
					**  No more C++ style comments.
					*/
					pcommentcpp = slen + 1;
				ENDIF;
			ENDIF;

			IF (pcomment <= slen AND pcomment < curpos) THEN
				pcomment = so.LocateString(match = '/*', startposition	= curpos);
				IF (pcomment = 0) THEN
					/*
					**  No more C-style comments.
					*/
					pcomment = slen + 1;
				ENDIF;
			ENDIF;

			IF (pcomment > slen AND pcommentcpp > slen) THEN
				/*
				**  No more comments.
				*/
				ENDLOOP;
			ENDIF;

			IF (pquote < pcomment AND pquote < pcommentcpp) THEN
				/*
				**  A quote is first; find end of quoted string.
				*/
				pquote = find_matching_quote(so = so, pos = pquote + 1);
				IF (pquote = 0) THEN
					CurExec.Trace(text = 'ERR_UNMATCHED_QUOTE');
					RETURN ER_FAIL;
				ENDIF;

				curpos = pquote + 1;

			ELSEIF (pcommentcpp < pcomment AND pcommentcpp < pquote) THEN
				/*
				**  A C++ style comment is first; skip to end of line.
				*/
				pend = so.LocateString(match = HC_NEWLINE, startposition = pcommentcpp + 2);
				IF (pend = 0) THEN
					pend = slen + 1;
				ENDIF;

				/*
				**  Copy up to the beginning of the comment AND
				**  CONTINUE searching after the comment end.
				*/
				pcopyto = pcommentcpp;
				pnext = pend + 1;

			ELSEIF (pcomment < slen) THEN
				/*
				**  We at the beginning of a C-style comment.
				**
				**  Copy up to the beginning of the comment AND CONTINUE
				**  searching after the beginning-of-comment delimiter.
				*/
				incomment = TRUE;
				pcopyto = pcomment;
				pnext = pcomment + 2;
			ENDIF;

		ELSE
			/*
			**  We are in a C-style comment AND looking for it's end.
			*/
			pend = so.LocateString(match = '*/', startposition = curpos);

			IF (pend = 0) THEN
				/*
				**  End of comment missing
				*/
				CurExec.Trace(text = 'ERR_UNTERMINATED_COMMENT');
				RETURN ER_FAIL;
			ENDIF;

			incomment = FALSE;
			curpos = pend + 2;
			pcopystart = curpos;

		ENDIF;

		IF (pcopyto != 0) THEN
			dest.ConcatString(string = so.ExtractString(startposition = pcopystart, length = pcopyto - pcopystart));
			curpos = pnext;
			pcopyto = 0;
			pcopystart = curpos;
		ENDIF;

	ENDWHILE;

	/*
	**  No more comments.  Copy the remainder of the script.
	*/
	IF (curpos <= slen) THEN
		dest.ConcatString(string = so.ExtractString(startposition = pcopystart));
	ENDIF;

	RETURN ER_OK;
}

PROCEDURE get_parameters
(
	so		= StringObject,
	params	= ARRAY of StringObject
) =
DECLARE
	curpos	= INTEGER NOT NULL;
	found_param	= INTEGER NOT NULL;
	nparams	= INTEGER NOT NULL default 0;
	param_name	= VARCHAR(200) NOT NULL;
	pcomma	= INTEGER NOT NULL;
	pdefault	= INTEGER NOT NULL;
	pend	= INTEGER NOT NULL;
	pequal	= INTEGER NOT NULL;
	plparen	= INTEGER NOT NULL;
	pmatch	= INTEGER NOT NULL;
	psemicolon	= INTEGER NOT NULL;
	pstart	= INTEGER NOT NULL;
	slen	= INTEGER NOT NULL;
	sparam	= VARCHAR(1000) NOT NULL;
ENDDECLARE
{
	params.Clear();

	slen = so.Length;
	curpos = 1;

	WHILE (curpos <= slen) DO
		/*
		**  Find the next parameter.
		*/
		pstart = curpos;
		found_param = FALSE;

		WHILE (found_param = FALSE) DO
			/*
			**  A parameter declaration may be terminated by either a comma OR a semicolon.
			**  However, there may be a comma in the declaration of a decimal type.
			**  IF we find an opening parenthesis before either a comma OR a semicolon,
			**  skip to the matching closing parenthesis.
			*/
			pcomma	 = so.LocateString(match = ',', startposition = curpos);
			psemicolon = so.LocateString(match = ';', startposition = curpos);
			plparen	= so.LocateString(match = '(', startposition = curpos);

			IF (pcomma = 0) THEN
				pcomma = slen + 1;
			ENDIF;

			IF (psemicolon = 0) THEN
				psemicolon = slen + 1;
			ENDIF;

			IF (plparen = 0) THEN
				found_param = TRUE;
			ELSE
				IF (plparen < pcomma AND plparen < psemicolon) THEN
					/*
					**  We have found an opening parenthesis before either a comma OR a semicolon.
					**  Skip to the matching closing parenthesis.
					*/
					pmatch = find_matching_paren(so = so, curpos = plparen + 1);
					IF (pmatch = 0) THEN
						CurExec.Trace(text = 'ERR_BAD_PROC_DECLARATION');
						RETURN ER_FAIL;
					ELSE
						curpos = pmatch + 1;
					ENDIF;
				ELSE
					found_param = TRUE;
				ENDIF;
			ENDIF;

		ENDWHILE;

		IF (pcomma < psemicolon) THEN
			pend = pcomma;
		ELSEIF (psemicolon < pcomma) THEN
			pend = psemicolon;
		ELSE
			pend = slen + 1;
		ENDIF;

		sparam = so.SubString(startposition = pstart, length = pend - pstart);
		curpos = pend + 1;

		IF (squeeze(sparam) = '') THEN
			CONTINUE;
		ENDIF;

		pequal = locate(sparam, '=');
		IF (pequal > size(sparam)) THEN
			/*
			**  There is no equals sign.
			*/
			CurExec.Trace(text = 'ERR_BAD_PROC_DECLARATION');
			RETURN ER_FAIL;
		ENDIF;

		param_name = squeeze(left(sparam, pequal - 1));

		IF (LENGTH(param_name) > 32) THEN
			CurExec.Trace(text = 'ERR_NAME_TOO_LONG');
			RETURN ER_FAIL;
		ENDIF;

		nparams = nparams + 1;
		params[nparams].Value = param_name;

	ENDWHILE;

	RETURN ER_OK;
}

PROCEDURE find_matching_paren
(
	so		= StringObject,
	curpos	= INTEGER NOT NULL
) =
DECLARE
	pendquote	= INTEGER NOT NULL;
	plparen	= INTEGER NOT NULL;
	pquote	= INTEGER NOT NULL;
	prparen	= INTEGER NOT NULL;
	ocount	= INTEGER NOT NULL default 1;
	slen	= INTEGER NOT NULL;
ENDDECLARE
{
	slen = so.length;

	WHILE (curpos < slen) DO
		prparen = so.LocateString(match = ')', startposition = curpos);
		IF (prparen = 0) THEN
			/*
			**  There are no more closing parentheses.
			*/
			RETURN 0;
		ENDIF;

		plparen = so.LocateString(match = '(', startposition = curpos);
		pquote = so.LocateString(match = HC_QUOTE, startposition = curpos);

		IF (pquote > 0) THEN
			IF (pquote < prparen AND (plparen > 0 AND pquote < plparen)) THEN
				pendquote = find_matching_quote(so = so, pos = pquote + 1);
				IF (pendquote > 0) THEN
					/*
					**  Skip the quoted string.
					*/
					curpos = pendquote + 1;
					CONTINUE;
				ELSE
					/*
					**  No matching quote on the line.
					*/
					RETURN 0;
				ENDIF;
			ENDIF;
		ENDIF;

		IF (plparen = 0) THEN
			curpos = prparen + 1;
			ocount = ocount - 1;
		ELSE
			IF (prparen < plparen) THEN
				curpos = prparen + 1;
				ocount = ocount - 1;
			ELSE
				curpos = plparen + 1;
				ocount = ocount + 1;
			ENDIF;
		ENDIF;

		IF (ocount = 0) THEN
			RETURN prparen;
		ENDIF;
	ENDWHILE;

	RETURN 0;
}

PROCEDURE find_matching_quote
(
	so		= StringObject,
	pos		= INTEGER NOT NULL
) =
DECLARE
	pendline	= INTEGER NOT NULL;
	pquote	= INTEGER NOT NULL;
ENDDECLARE
{
	pendline = so.LocateString(match = HC_NEWLINE, startposition = pos);
	IF (pendline = 0) THEN
		pendline = so.length + 1;
	ENDIF;

	pquote = so.LocateString(match = HC_QUOTE, startposition = pos);
	IF (pquote > 0 AND pquote < pendline) THEN
		RETURN pquote;
	ENDIF;

	RETURN 0;
}

PROCEDURE substitute_in_string
(
	vc		= VARCHAR(2000) NOT NULL,
	sfind	= VARCHAR(100) NOT NULL,
	sreplace	= VARCHAR(100) NOT NULL
) =
DECLARE
	curpos	= INTEGER NOT NULL;
	pfind	= INTEGER NOT NULL;
	sold	= StringObject;
	snew	= StringObject;
ENDDECLARE
{
	sold.value = vc;
	snew.value = '';

	curpos = 1;
	pfind = sold.LocateString(match = sfind, startposition = curpos);
	WHILE (pfind > 0) DO
		snew.ConcatString(string = sold.ExtractString(startposition = curpos, length = pfind - curpos));
		snew.concatvarchar(text = sreplace);

		curpos = pfind + LENGTH(sfind);
		pfind = sold.LocateString(match = sfind, startposition = curpos);
	ENDWHILE;

	snew.ConcatString(string = sold.ExtractString(startposition = curpos));

	RETURN snew.value;
}

			/**
**	Frame:	top
**	Description:
**		This frame is used to generate the JSON used for configuring an application
**		containing 4GL procedures which can be called via the JSON-RPC 2.0 interface
**		(JsonRpcRequest() methods of RemoteServer/JsonHandler classes).
**		The JSON file should be placed in the %II_SYSTEM%\ingres\files\orjsonconfig directory
**		( $II_SYSTEM/ingres/files/orjsonconfig on Unix).
**
**	Usage:
**		- Select the application
**		- Optionally set attributes for the JsonHandler
**		- Select the procedures which should be accessible via JsonRpcRequest()
**			- Optionally set the "byref_use" default for the selected procedures.
**			  This will be used as the value for "byref_use" of all parameters of the procedure.
**		- Click on the "Create JSON for registration >>" button, which will show the JSON in the
**		  multiline entryfield.
**		- Write the JSON produced to a file by using the "Write JSON to file ..." button.
**		  Alternatively you can copy (the relevant parts of) the JSON to the clipboard
**		  by marking it and hitting Ctrl+C. Then you can paste it where needed (in some editor).
**		  Using an editor you can also manually edit the JSON produced afterwards,
**		  e.g. to set different "byref_use" values for individual parameters.
**
*/

INITIALIZE()=
DECLARE
	i_status = INTEGER NOT NULL;
	i_sel = INTEGER NOT NULL;
	i = INTEGER NOT NULL DEFAULT 2;
	k = INTEGER NOT NULL;
	ci = ARRAY OF ChoiceItem DEFAULT NULL;
	appsrc = AppSource DEFAULT NULL;
	cache = DBCache;
	lockblk = lockblock;
	appname = VARCHAR(32) NOT NULL DEFAULT '*';
	procsrc = Proc4GLSource DEFAULT NULL;
	params = ARRAY OF StringObject DEFAULT NULL;
	reply = StringObject;
	vstr = VARCHAR(6) NOT NULL;
	jo_app = JsonObject DEFAULT NULL;
	jo_procs = JsonObject DEFAULT NULL;
	jo_proc = JsonObject DEFAULT NULL;
	jo_procarr = JsonArray DEFAULT NULL;
	jo_params = JsonObject DEFAULT NULL;
	jo_param = JsonObject DEFAULT NULL;
	jo_paramsarr = JsonArray DEFAULT NULL;
	jb_aacn = JsonBoolean;
	jb_iua = JsonBoolean;
	jb = JsonBoolean DEFAULT NULL;
	jn_maxnl = JsonNumber;
	js = JsonString DEFAULT NULL;
ENDDECLARE
{
	COMMIT;
	SET LOCKMODE SESSION WHERE READLOCK=NOLOCK;
	COMMIT;
	ci = FIELD(app).ValueList.ChoiceItems;
	SELECT ci[i].EnumText = LOWERCASE(entity_name), ci[i].EnumValue=entity_id
	FROM ii_entities a
	WHERE entity_type = 'appsource' AND version_number=0 AND entity_id IN
	(SELECT folder_id FROM ii_entities b WHERE b.entity_type='proc4glsource' and version_number=-1)
	ORDER BY 1
	BEGIN
		i=i+1;
	END;
	COMMIT;
}

ON SETVALUE app,
ON SETVALUE ptype =
{
	tf.Clear();
	jsonfld.Value = '';
	appname = FIELD(app).CurEnumText;
	IF app = 0 THEN
		RESUME;
	ENDIF;

	appsrc = cache.GetDBAppl(NULL, appname, -1, 3, lockblk, NULL);

	IF appsrc IS NULL
	THEN
		MESSAGE 'Could not get AppSource for application '+appname;
		RESUME;
	ENDIF;

	i=1;
	SELECT tf[i].procname = LOWERCASE(entity_name), vstr = LEFT(value_string,6)
	FROM ii_entities e, ii_components c
	WHERE entity_type = 'proc4glsource' AND version_number=-1 AND folder_id=:app
	AND c.entity_id=e.entity_id
	ORDER BY 1
	BEGIN
		IF vstr='GSCPP(' OR LEFT(vstr, 5)='GSCP('
			OR tf[i].procname = 'ascp_load_metagen'
			OR tf[i].procname = 'definebpm'
			OR tf[i].procname = 'getmetadatainterface'
		THEN
			tf[i].gscp = TRUE;
		ENDIF;
		IF ptype = 0 OR (ptype=1 and tf[i].gscp = FALSE)
		OR (ptype=2 and tf[i].gscp = TRUE) THEN
			i=i+1;
		ELSE
			tf.RemoveRow(rownumber=i);
		ENDIF;
	END;
	COMMIT;
}

ON CLICK selall_btn =
{
	FOR i=1 TO tf.LastRow DO
		tf[i].sel = TRUE;
	ENDFOR;
}

ON CLICK unselall_btn =
{
	FOR i=1 TO tf.LastRow DO
		tf[i].sel = FALSE;
	ENDFOR;
}

ON CLICK json_btn =
{
	IF app=0 THEN
		MESSAGE 'No application has been selected yet.';
		RESUME;
	ENDIF;
	jsonfld.Value = '';
	jo_app = JsonObject.Create();
	jb_aacn.Value = alwaysaddclassname;
	jo_app.AddMember(name = 'alwaysaddclassname', value = jb_aacn);
	jb_iua.Value = ignoreunknownattributes;
	jo_app.AddMember(name = 'ignoreunknownattributes', value = jb_iua);
	jn_maxnl.SetValue(value = maxnestinglevel);
	jo_app.AddMember(name = 'maxnestinglevel', value = jn_maxnl);
	jo_procs = JsonObject.Create();
	jo_app.AddMember(name = 'registeredprocs', value = jo_procs);
	jo_procarr = JsonArray.Create();
	jo_procs.AddMember(name = 'rows', value = jo_procarr);
	i_sel = 0;
	FOR i=1 TO tf.LastRow DO
		IF tf[i].sel = TRUE THEN
			procsrc = Proc4GLSource(cache.GetDBComp(appsrc, tf[i].procname, -1, NULL, 3, lockblk, NULL));
			IF procsrc IS NULL
			THEN
				MESSAGE 'Could not get Proc4GLSource for procedure '+tf[i].procname;
				RESUME;
			ENDIF;

			params = get_procedure_parameter_names( procsrc = procsrc);
			IF params IS NULL THEN
				MESSAGE 'Could not get parameter names for procedure '+tf[i].procname;
				RESUME;
			ENDIF;

			jo_proc = JsonObject.Create();
			i_sel = i_sel+1;
			jo_procarr.Items[i_sel] = jo_proc;
			js = JsonString.Create();
			js.Value.Value = tf[i].procname;
			jo_proc.AddMember(name = 'name', value = js);

			IF params.LastRow>0 THEN
				jo_params = JsonObject.Create();
				jo_proc.AddMember(name = 'params', value = jo_params);
				jo_paramsarr = JsonArray.Create();
				jo_params.AddMember(name = 'rows', value = jo_paramsarr);
				FOR k=1 TO params.LastRow DO
					jo_param = JsonObject.Create();
					jo_paramsarr.Items[k] = jo_param;
					js = JsonString.Create();
					js.Value.Value = LOWERCASE(params[k].Value);
					jo_param.AddMember(name = 'name', value = js);
					jb = JsonBoolean.Create();
					jb.Value = tf[i].bruse_def;
					jo_param.AddMember(name = 'byref_use', value = jb);
				ENDFOR;
			ENDIF;
		ENDIF;
	ENDFOR;
	CurSession.JsonHandler.Write(value = jo_app, json = jsonfld, indented = TRUE, sortobjectmembers=TRUE);
	FIELD(jsonfld).UpdField();
	IF i_sel = 0 THEN
		MESSAGE 'WARNING: No procedures have been selected.';
	ENDIF;
}

ON CLICK writefile_btn =
{
	IF app=0 THEN
		MESSAGE 'No application has been selected yet.';
		RESUME;
	ENDIF;
	IF jsonfld.Value = '' THEN
		MESSAGE 'JSON has not been created yet.';
		RESUME;
	ENDIF;
	reply.Value = appname+'.json';
	IF CurFrame.FilePopup(reply = reply, operation=FOP_SAVE, defaultextension='.json') = PU_OK THEN
		CurSession.JsonHandler.WriteFile(value = jo_app, filename = reply.Value, indented = TRUE, sortobjectmembers=TRUE);
	ENDIF;
}

ON SETVALUE maxnestinglevel =
{
	IF maxnestinglevel<0 THEN
		MESSAGE 'MaxNestingLevel must be >= 0!';
		RESUME;
	ELSEIF maxnestinglevel>0 THEN
		MESSAGE 'WARNING: MaxNestingLevel>0 will disallow support for circular references (using "$id" and "$ref")!';
	ENDIF;
}

